fighter_features_info = c.execute("SELECT level, proficiency_bonus, features FROM FighterFeaturesByLevel").fetchall()

monk_features_info = c.execute("SELECT level, proficiency_bonus, martial_arts, ki_points, unarmored_movement, features FROM MonkFeaturesByLevel").fetchall()

