ranger_features_info = c.execute("SELECT level, proficiency_bonus, features, spells_known, first_level_spells, second_level_spells, third_level_spells, fourth_level_spells, fifth_level_spells FROM RangerFeaturesByLevel").fetchall()

rogue_features_info = c.execute("SELECT level, proficiency_bonus, sneak_attack, features FROM RogueFeaturesByLevel").fetchall()

sorcerer_features_info = c.execute("SELECT level, proficiency_bonus, sorcery_points, features, cantrips_known, spells_known, first_level_spells, second_level_spells, third_level_spells, fourth_level_spells, fifth_level_spells, sixth_level_spells, seventh_level_spells, eighth_level_spells, ninth_level_spells FROM SorcererFeaturesByLevel").fetchall()

warlock_features_info = c.execute("SELECT level, proficiency_bonus, features, cantrips_known, spells_known, spell_slots, slot_level, invocations_known FROM WarlockFeaturesByLevel").fetchall()

wizard_features_info = c.execute("SELECT level, proficiency_bonus, features, cantrips_known, first_level_spells, second_level_spells, third_level_spells, fourth_level_spells, fifth_level_spells, sixth_level_spells, seventh_level_spells, eighth_level_spells, ninth_level_spells FROM WizardFeaturesByLevel").fetchall()

class_equipment = c.execute("SELECT class_name, weapon, or_weapon, last_weapon, weapon_two, or_weapon_two, last_weapon_two, armor, or_armor, last_armor, pack, or_pack, last_pack, misc, misc_two, misc_three FROM ClassEquipment").fetchall()

barbarian_features_description = c.execute("SELECT feature_name, feature_description FROM BarbarianFeaturesDescription").fetchall()

path_of_the_berserker = c.execute("SELECT level, feature_name, feature_description FROM PathOfTheBerserker").fetchall()

