#function to generate sets of tuples that give the character stats
# in above stats_order_list from class_stats_structure ditionary


def generate_stats(class_name):
    stats = [roll_stat_dice(4, 6) for i in range(6)]
    sortedstats = sorted(stats)

    class_stat = class_stats_structure[class_name]
    stat = dict(zip(class_stat, sortedstats))
    reordered_stats = {k: stat[k] for k in stats_order_list}

    return reordered_stats
    
#each race has their own modifiers, this adds them
def race_modifiers(class_name, race_name):
    generate_stats_values = generate_stats(class_name)
    if race_name == 'Dragonborn':
        generate_stats_values['strength'] += 2
        generate_stats_values['charisma'] += 1 
    elif race_name == 'Dwarf':
        generate_stats_values['constitution'] += 2
    elif race_name == 'Elf':
        generate_stats_values['dexterity'] += 2
    elif race_name == 'Gnome':
        generate_stats_values['intelligence'] += 2
    #half elf also has +1 to two stats of your choice
    elif race_name == 'Half-Elf':
        generate_stats_values['charisma'] += 2
    elif race_name == 'Halfling':
        generate_stats_values['dexterity'] += 2
    elif race_name == 'Half-Orc':
        generate_stats_values['strength'] += 2
        generate_stats_values['constitution'] += 1
    elif race_name == 'Human':
        generate_stats_values['strength'] += 1
        generate_stats_values['dexterity'] += 1
        generate_stats_values['constitution'] += 1
        generate_stats_values['intelligence'] += 1
        generate_stats_values['wisdom'] += 1
        generate_stats_values['charisma'] += 1
    elif race_name == 'Tiefling':
        generate_stats_values['intelligence'] += 1
        generate_stats_values['charisma'] += 2

    #this turns the dict into a set of strings
    stats_list = list(generate_stats_values.items())
    for words, number in stats_list:
        print(words, number)

        #this adds the above class_stat_modifier to output
        for key, value in class_stat_modifier.items():
            if key == number:
                print(value)
    
    return generate_stats_values

